export const fetchAiMove = async (board, phase) => {
  try {
    const response = await fetch("http://localhost:8080/api/ai-move", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ board, phase }),
    });

    const data = await response.json();
    console.log("AI Move Response:", data); // Log to check response structure

    return data; // Return the AI move
  } catch (error) {
    console.error("Error fetching AI move:", error);
    return null;
  }
};

  
  export const validateMove = async (from, to, board) => {
    try {
      const response = await fetch("http://localhost:8080/api/validate-move", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ from, to, board }),
      });
      return await response.json();
    } catch (error) {
      console.error("Error validating move:", error);
      return false;
    }
  };
