import React, { useState, useEffect } from 'react';
import GameBoard from './GameBoard';
import GameStatus from './GameStatus';

function App() {
  const [status, setStatus] = useState('Game has not started yet.');
  const [board, setBoard] = useState(Array(3).fill(Array(3).fill(null)));

  useEffect(() => {
    fetch('http://localhost:8080/game/start')
      .then(response => response.text())
      .then(data => setStatus(data));
  }, []);

  const handleCellClick = (row, col) => {
    fetch(`http://localhost:8080/game/move?row=${row}&col=${col}&player=X`)
      .then(response => response.text())
      .then(data => setStatus(data));

    // Update the board state locally here if necessary
  };

  return (
    <div className="App">
      <h1>Advanced Tic-Tac-Toe</h1>
      <GameStatus status={status} />
      <GameBoard board={board} onCellClick={handleCellClick} />
    </div>
  );
}

export default App;
