import React, { useState, useEffect } from "react";

import GameBoard from "./components/GameBoard";
import GameStatus from "./GameStatus";
import { fetchAiMove, validateMove } from "./utils/api";
import { checkWinner } from "./utils/validations";
import { PLAYER, AI } from "./utils/constants";

const App = () => {
  const [board, setBoard] = useState(Array(3).fill(null).map(() => Array(3).fill(null)));
  const [phase, setPhase] = useState("place");
  const [playerMoves, setPlayerMoves] = useState(0);
  const [aiMoves, setAiMoves] = useState(0);
  const [status, setStatus] = useState("Your turn to place your coins!");
  const [selectedPiece, setSelectedPiece] = useState(null);

  const handleCellClick = async (row, col) => {
    // Prevent undefined board or invalid state access
    if (!Array.isArray(board) || board.length !== 3 || !board[0].length === 3) {
      console.error("Board is not properly initialized");
      return;
    }

    if (phase === "place") {
      if (playerMoves < 3 && !board[row][col]) {
        const newBoard = board.map((r, i) =>
          r.map((c, j) => (i === row && j === col ? PLAYER : c))
        );
        setBoard(newBoard);
        setPlayerMoves(playerMoves + 1);
        setStatus("AI is thinking...");

        if (playerMoves + 1 === 3 && aiMoves === 3) {
          setPhase("move");
          setStatus("Select a coin to move!");
        } else {
          const aiMove = await fetchAiMove(newBoard, "place");
          if (aiMove) {
            newBoard[aiMove.row][aiMove.col] = AI;
            setAiMoves(aiMoves + 1);
            setBoard(newBoard);
          }
        }
      }
    } else if (phase === "move") {
      if (selectedPiece) {
        if (selectedPiece.row === row && selectedPiece.col === col) {
          setSelectedPiece(null);
          setStatus("Select a coin to move!");
        } else {
          const isValid = await validateMove(selectedPiece, { row, col }, board);
          if (isValid) {
            const newBoard = board.map((r, i) =>
              r.map((c, j) => {
                if (i === selectedPiece.row && j === selectedPiece.col) return null;
                if (i === row && j === col) return PLAYER;
                return c;
              })
            );
            setBoard(newBoard);
            setSelectedPiece(null);
            setStatus("AI is thinking...");
            const aiMove = await fetchAiMove(newBoard, "move");
            if (aiMove) {
              newBoard[aiMove.to.row][aiMove.to.col] = AI;
              newBoard[aiMove.from.row][aiMove.from.col] = null;
              setBoard(newBoard);
            }
          } else {
            setStatus("Invalid move! Select again.");
          }
        }
      } else if (board[row][col] === PLAYER) {
        setSelectedPiece({ row, col });
        setStatus("Select a destination.");
      }
    }
  };

  useEffect(() => {
    const winner = checkWinner(board);
    if (winner) {
      setStatus(`${winner} wins!`);
      setPhase("end");
    }
  }, [board]);

  return (
    <div className="app">
      <h1>Advanced Tic-Tac-Toe</h1>
      <GameStatus status={status} />
      <GameBoard board={board} selectedPiece={selectedPiece} handleCellClick={handleCellClick} />
      <button onClick={() => window.location.reload()}>New Game</button>
    </div>
  );
};

export default App;
