import React, { useState, useEffect } from 'react';
import GameBoard from './components/GameBoard.js';
import GameStatus from './GameStatus';

function App() {
  const [status, setStatus] = useState('Game has not started yet.');
  const [board, setBoard] = useState(Array(3).fill(Array(3).fill(null)));

  useEffect(() => {
    fetch('http://localhost:8080/game/start')
      .then(response => response.text())
      .then(data => setStatus(data));
  }, []);

  // In your React app (App.js)
const handleCellClick = async (row, col) => {
    const player = 'X';  // Assuming X is always the current player
    const response = await fetch('http://localhost:8080/game/move', {
      method: 'POST',  // Use POST method instead of GET
      headers: {
        'Content-Type': 'application/json',  // Ensure you're sending JSON
      },
      body: JSON.stringify({ row, col, player }),  // Send the move as JSON in the body
    });
  
    const result = await response.json();
    console.log(result);
  };
  

  return (
    <div className="App">
      <h1>Advanced Tic-Tac-Toe</h1>
      <GameStatus status={status} />
      <GameBoard board={board} onCellClick={handleCellClick} />
    </div>
  );
}

export default App;
